#!/usr/bin/env node
const pkg = require("./package.json");
try {
  if (process.argv && process.argv.length) {
    process.argv.forEach(a => {
      if (a.includes("-v") || a.includes("--version")) {
        console.log(`version: ${pkg.version}`);
      } else if (a.includes("--update") || a.includes("-u")) {
        var exec = require("child_process").exec;
        exec("npm i -g bcw");
      } else if(a.includes('-h')) {
        printHelp();
      }
    });
  } else {
    printHelp()
  }
} catch (error) {
  console.error(error);
}

function printHelp() {
  console.log(`Usage: 

bcw-create: creates a new project from templates
bcw-serve (-p || --port): initailizes the static server with optional specification for the port to serve on
bcw-update: installs the latest version of the bcw tool`);
}

